/*Table structure for table `order` */

DROP TABLE IF EXISTS `order`;

CREATE TABLE `order` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `open_id` varchar(150) NOT NULL COMMENT '微信id',
  `project_id` int(11) NOT NULL COMMENT '项目id',
  `project_name` varchar(200) DEFAULT NULL COMMENT '项目名称',
  `user_name` varchar(150) DEFAULT NULL,
  `phone` varchar(15) DEFAULT NULL COMMENT '电话',
  `email` varchar(100) DEFAULT NULL COMMENT '邮件',
  `address` varchar(300) DEFAULT NULL COMMENT '地址',
  `post_code` varchar(10) DEFAULT NULL COMMENT '邮编',
  `status` int(11) DEFAULT '0' COMMENT '-1:冻结,0:待处理,1:处理中，2:处理完成',
  `create_date` datetime DEFAULT NULL,
  `update_date` datetime DEFAULT NULL,
  `subscribe_Date` datetime NOT NULL COMMENT '下单时间',
  `parent_open_id` varchar(150) DEFAULT NULL COMMENT '推荐人',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

