/*Table structure for table `project` */

DROP TABLE IF EXISTS `project`;

CREATE TABLE `project` (
  `project_id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` int(11) NOT NULL,
  `short_desc` text NOT NULL COMMENT '短描述',
  `min_price` decimal(10,0) DEFAULT NULL COMMENT '最低价',
  `max_price` decimal(10,0) DEFAULT NULL COMMENT '最高价',
  `category_name` varchar(100) NOT NULL COMMENT '项目名称',
  `img_path` varchar(300) DEFAULT NULL COMMENT '图片',
  `detail` text COMMENT '长描述',
  `operation` varchar(1000) DEFAULT NULL COMMENT '手术',
  `treatment` varchar(1000) DEFAULT NULL COMMENT '治疗',
  `recure` varchar(1000) DEFAULT NULL COMMENT '康复',
  `content` longtext COMMENT '内容',
  `advantage` varchar(1000) DEFAULT NULL COMMENT '优点',
  `disadvantage` varchar(1000) DEFAULT NULL COMMENT '缺点',
  `concern` float DEFAULT '5' COMMENT '关注度',
  `safe` float DEFAULT '5' COMMENT '安全度',
  `complex` float DEFAULT '3' COMMENT '复杂度',
  `status` tinyint(4) DEFAULT '1' COMMENT '0:停止 ,1:激活',
  `book_num` int(11) DEFAULT '0' COMMENT '预约数',
  PRIMARY KEY (`project_id`)
) ENGINE=MyISAM AUTO_INCREMENT=161 DEFAULT CHARSET=utf8;

